    def hd(self):
        self.ag=self.lll.text()
        self.cho=self.hhh.text()
        self.trestbps=self.rrr.text()
        self.thalach=self.ppp.text()
        self.oldpea=self.kkk.text()
        if self.a1.isChecked() ==True:
            xx=1
        if self.a0.isChecked() == True:
            xx=0
            
        if self.c1.isChecked() == True:
            cc=1
        if self.c0.isChecked() == True:
            cc=0
            
        if self.b1.isChecked() == True:
            b=0
        if self.b2.isChecked() == True:
            bb=1
        if self.b3.isChecked() == True:
            bb=2
        if self.b4.isChecked() == True:
            bb=3
            
        if self.d0.isChecked() == True:
            dd=0
        if self.d1.isChecked() == True:
            dd=1
        if self.d2.isChecked() == True:
            dd=2
            
        if self.e1.isChecked() == True:
            ee=0
        if self.e2.isChecked() == True:
            ee=1
        if self.e3.isChecked() == True:
            ee=2
        if self.radioButton.isChecked() == True:
            ee=3
            
        if self.t1.isChecked() == True:
            tt=1
        if self.t2.isChecked() == True:
            tt=2
        if self.t3.isChecked() == True:
            tt=3
            
        if self.s1.isChecked() == True:
            ss=0
        if self.s2.isChecked() == True:
            ss=1
        if self.s2.isChecked() == True:
            ss=2
            
        if self.h1.isChecked() == True:
            hh=1
        if self.h2.isChecked() == True:
            hh=0
        

        data_x = pd.read_csv("heart.csv")
        df_x = data_x[['age', 'sex','cp','trtbps','chol','fbs','restecg','thalachh','exng','oldpeak','slp','caa','thall']].values.tolist()
        df_y = data_x[['output']].values.tolist()
        clf = tree.DecisionTreeClassifier()
        clf = clf.fit(df_x,df_y)
        new_x = [[int(self.ag),xx,bb,int(self.trestbps),int(self.cho),cc,dd,int(self.thalach),hh,float(self.oldpea),ss,ee,tt]]
        answer = clf.predict(new_x)
        self.lineEdit.setText(str(answer))

